import { useState } from 'react';
import './App.css';

const App = () => {
  const[user, setUser] = useState("");
  const[password, setPassword] = useState("");
  const[isLogged, setIsLogged] = useState(false);

  const handleLoginClick = () => {
    setIsLogged(true);
  };



  return(
    <div>
      <label>
        User
        <input value={user} onChange={({target : {value}}) => setUser(value)}></input>
      </label>
      <br/>
      <br/>
      <label>
        Password
        <input type="password" value={password} onChange={({target : {value}}) => setPassword(value)}></input>
      </label>
      <br/>
      <br/>
      <button onClick={handleLoginClick}>
      Iniciar sesi√≥n
      </button>
      {isLogged ?  <h2>Logueado exitosamente</h2> : undefined}
      {isLogged &&  <h2>Logueado exitosamente 2</h2>}
      {isLogged &&  <Footer/>}
    </div>
  );
}

export default App;
